import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import CollectionsOverviewContainer from '../../components/collections-overview/CollectionsOverviewContainer';
import CollectionPage from '../collection/CollectionPage';
import WithSpinner from '../../components/WithSpinner/WithSpinner';

import { fetchCollectionsStartAsync } from '../../redux/actions/shopActions';
import {
   selectCollectionIsFetching,
   selectIsCollectionsLoaded
} from '../../redux/selectors/shopSelectors';

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const ShopPage = ({
   match,
   fetchCollectionsStartAsync,
   isCollectionsLoaded
}) => {
   useEffect(() => {
      fetchCollectionsStartAsync();
   }, []);

   return (
      <main className='shop-page'>
         <Route
            exact
            path={`${match.path}`}
            component={CollectionsOverviewContainer}
         />
         <Route
            path={`${match.path}/:collectionId`}
            render={props => (
               <CollectionPageWithSpinner
                  isLoading={!isCollectionsLoaded}
                  {...props}
               />
            )}
         />
      </main>
   );
};

ShopPage.propTypes = {
   match: PropTypes.object.isRequired,
   fetchCollectionsStartAsync: PropTypes.func.isRequired,
   isCollectionsLoaded: PropTypes.bool.isRequired
};

const mapStateToProps = createStructuredSelector({
   isCollectionsLoaded: selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
   fetchCollectionsStartAsync: collectionsMap =>
      dispatch(fetchCollectionsStartAsync(collectionsMap))
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
