import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import CollectionsOverview from '../../components/collections-overview/CollectionsOverview';
import CollectionPage from '../collection/CollectionPage';
import WithSpinner from '../../components/WithSpinner/WithSpinner';

import { fetchCollectionsStartAsync } from '../../redux/actions/shopActions';
import { selectCollectionIsFetching } from '../../redux/selectors/shopSelectors';

import {
   firestore,
   convertCollectionsSnapshotToMap
} from '../../firebase/utils';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const ShopPage = ({ match, updateCollections, isFetching }) => {
   useEffect(() => {
      fetchCollectionsStartAsync();
   }, []);

   return (
      <main className='shop-page'>
         <Route
            exact
            path={`${match.path}`}
            render={props => (
               <CollectionsOverviewWithSpinner
                  isLoading={isLoading}
                  {...props}
               />
            )}
         />
         <Route
            path={`${match.path}/:collectionId`}
            render={props => (
               <CollectionPageWithSpinner isLoading={isLoading} {...props} />
            )}
         />
      </main>
   );
};

ShopPage.propTypes = {
   match: PropTypes.object.isRequired,
   updateCollections: PropTypes.func.isRequired,
   isFetching: PropTypes.bool.isRequired
};

const mapStateToProps = createStructuredSelector({
   isFetching: selectCollectionIsFetching
});

const mapDispatchToProps = dispatch => ({
   updateCollections: collectionsMap =>
      dispatch(updateCollections(collectionsMap))
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
