import { CartActionTypes } from '../types';

const intitialState = {
   isHidden: true
};

export default (state = intitialState, action) => {
   const { type, payload } = action;

   switch (type) {
      case CartActionTypes.TOGGLE_CART_DROPDOWN:
         return {
            ...state,
            isHidden: !state.isHidden
         };

      default:
         return state;
   }
};
