import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionsOverviewContainer from '../../components/collections-overview/CollectionsOverviewContainer';
import CollectionsPageContainer from '../collection/CollectionsPageContainer';

import { fetchCollectionsStartAsync } from '../../redux/actions/shopActions';

const ShopPage = ({ match, fetchCollectionsStartAsync }) => {
   useEffect(() => {
      fetchCollectionsStartAsync();
   }, []);

   return (
      <main className='shop-page'>
         <Route
            exact
            path={`${match.path}`}
            component={CollectionsOverviewContainer}
         />
         <Route
            path={`${match.path}/:collectionId`}
            component={CollectionPageContainer}
         />
      </main>
   );
};

ShopPage.propTypes = {
   match: PropTypes.object.isRequired,
   fetchCollectionsStartAsync: PropTypes.func.isRequired
};

const mapDispatchToProps = dispatch => ({
   fetchCollectionsStartAsync: collectionsMap =>
      dispatch(fetchCollectionsStartAsync(collectionsMap))
});

export default connect(null, mapDispatchToProps)(ShopPage);
